import React, { useEffect } from 'react';
import { CheckCircle, AlertCircle, Loader } from 'lucide-react';
import { documentAPI } from '../services/api';

const InvoiceProcessor = ({ files, isProcessing, onProcessed }) => {
  useEffect(() => {
    if (files.length > 0 && isProcessing) {
      processInvoices();
    }
  }, [files, isProcessing]);

  const processInvoices = async () => {
    const results = [];

    for (const fileObj of files) {
      try {
        // Convert file to base64
        const base64 = await fileToBase64(fileObj.file);

        // Call your lambda function
        const response = await documentAPI.processDocument(base64, fileObj.name, 'invoice');

        if (response.success) {
          results.push({
            id: fileObj.id,
            fileName: fileObj.name,
            status: 'success',
            data: response.structured_data,
            rawText: response.raw_text,
            confidence: response.extraction_confidence,
            validation: response.validation
          });
        } else {
          results.push({
            id: fileObj.id,
            fileName: fileObj.name,
            status: 'error',
            error: response.error || 'Processing failed'
          });
        }
      } catch (error) {
        results.push({
          id: fileObj.id,
          fileName: fileObj.name,
          status: 'error',
          error: error.message || 'Network error'
        });
      }
    }

    onProcessed(results);
  };

  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        const base64 = reader.result.split(',')[1]; // Remove data:mime;base64, prefix
        resolve(base64);
      };
      reader.onerror = error => reject(error);
    });
  };

  if (!isProcessing) return null;

  return (
    <div className="bg-white rounded-lg border border-gray-200 p-6 mb-6">
      <div className="space-y-4">
        <div className="flex items-center space-x-3">
          <Loader className="h-6 w-6 text-primary-600 animate-spin" />
          <h3 className="text-lg font-medium text-gray-900">
            Processing {files.length} files...
          </h3>
        </div>

        <div className="space-y-3">
          {files.map((file, index) => (
            <div key={file.id} className="flex items-center justify-between py-2">
              <span className="text-sm text-gray-600">{file.name}</span>
              <div className="flex items-center space-x-2">
                <div className="w-24 bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-primary-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: '100%' }}
                  />
                </div>
                <Loader className="h-4 w-4 text-primary-600 animate-spin" />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default InvoiceProcessor;
